import { useEffect, useState, useCallback } from 'react';
import { useNavigate, Link, useParams, useNavigation } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import Swal from 'sweetalert2';
import axios from 'axios';
import Parser from 'html-react-parser';
import { setAuth } from '../actions/areaFilterActions';
import { ReactComponent as View } from '../images/view.svg';
import LikeOff from '../images/perpett-off.png';
import LikeOn from '../images/community-like-on.png';
import Button from '../components/Button/Button';
import Input from '../components/Input/Input';
import dateCalculate from '../components/dateCalculate';
import Comment from '../components/Comment';
import profile from '../images/profile.png';

function Post() {
  const [post, setPost] = useState([]);
  const [isPending, setIsPending] = useState(false);
  const [comments, setComments] = useState([]);
  const [commentInput, setCommentInput] = useState('');
  const [like, setLike] = useState(false);
  const [countLike, setCountLike] = useState(0);
  const [images, setImages] = useState([]);
  const [writerInfo, setWriterInfo] = useState([]);
  const [commentError, setCommentError] = useState('');
  const [kinderName, setKinderName] = useState([]);
  const navigate = useNavigate();
  const { postId } = useParams();
  const { id } = useParams();
  const auth = useSelector(state => state.auth);
  const curProfile = useSelector(state => state.curProfile);

  const dispatch = useDispatch();
  const navi = useNavigate();

  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/api/post/${postId}/comment`)
      .then(response => {
        setIsPending(true);
        // setPost(response.data[0]);
        setComments(response.data.data);
      })
      .catch(error => {
        Swal.fire({
          icon: 'error',
          text: `error! ${error}`,
          confirmButtonColor: '#FFD337',
        });
        setIsPending(false);
      });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  function changeInput(e) {
    setCommentInput(e.target.value);
    if (e.target.value.length !== 0) {
      setCommentError('');
    } else {
      setCommentError('ÎåìÍ∏Ä ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
    }
  }

  function postComment() {
    if (!auth) {
      Swal.fire({
        icon: 'error',
        text: 'Î°úÍ∑∏Ïù∏ÏùÑ Î®ºÏ†Ä Ìï¥Ï£ºÏÑ∏Ïöî‚ùóÔ∏è',
        confirmButtonColor: '#FFD337',
      }).then(res => {
        navi('/login');
      });
    }
    if (commentInput.length !== 0) {
      const data = {
        postId,
        content: commentInput,
      };

      axios
        .post(
          `${process.env.REACT_APP_API_URL}/api/post/${postId}/comment`,
          data,
          {
            headers: {
              Authorization: localStorage.getItem('token'),
            },
          },
        )
        .then(response => {
          window.location.reload();
        })
        .catch(error => {
          if (error.response && error.response.status === 401) {
            Swal.fire({
              icon: 'error',
              text: 'ÌÜ†ÌÅ∞Ïù¥ ÎßåÎ£åÎêòÏóàÏäµÎãàÎã§. Ïû¨Î°úÍ∑∏Ïù∏ Ìï¥Ï£ºÏÑ∏Ïöî‚ùóÔ∏è',
              confirmButtonColor: '#FFD337',
            });
            dispatch(setAuth(false));
            localStorage.removeItem('token');
          }
        });
    } else {
      setCommentError('ÎåìÍ∏Ä ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
    }
  }

  // Ïú†ÏπòÏõê Ï†ïÎ≥¥
  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/api/community/${id}`)
      .then(response => {
        setKinderName(response.data.data);
      });
  }, [id]);

  // Í≤åÏãúÍ∏Ä Ï†ïÎ≥¥
  useEffect(() => {
    window.scroll(0, 0);
    const headers = {
      headers: {
        Authorization: localStorage.getItem('token'),
        'Content-Type': 'multipart/form-data',
      },
    };
    axios
      .get(
        `${process.env.REACT_APP_API_URL}/api/community/${id}/post/${postId}`,
        localStorage.getItem('token') && headers,
      )
      .then(response => {
        setWriterInfo(response.data.data);
        setPost(response.data.data);
        setCountLike(response.data.data.likeCount);
        setLike(response.data.data.like);
        setImages(response.data.data.images);
      })
      .catch(error => {
        if (error.response && error.response.status === 401) {
          Swal.fire({
            icon: 'error',
            text: 'ÌÜ†ÌÅ∞Ïù¥ ÎßåÎ£åÎêòÏóàÏäµÎãàÎã§. Ïû¨Î°úÍ∑∏Ïù∏ Ìï¥Ï£ºÏÑ∏Ïöî‚ùóÔ∏è',
            confirmButtonColor: '#FFD337',
          });
          dispatch(setAuth(false));
          localStorage.removeItem('token');
        } else {
          Swal.fire({
            icon: 'error',
            text: `error! ${error}`,
            confirmButtonColor: '#FFD337',
          });
        }
      });
  }, [setPost, postId, id, auth, dispatch]);

  // Í∏ÄÏÇ≠Ï†ú
  const handleDelete = useCallback(() => {
    Swal.fire({
      text: 'Í≤åÏãúÍ∏ÄÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#FFD337',
      cancelButtonColor: '#ffffff',
      confirmButtonText: 'ÎÑ§',
      cancelButtonText: '<span style="color:#000000">ÏïÑÎãàÏò§<span>',
    }).then(result => {
      if (result.isConfirmed) {
        if (!auth) {
          Swal.fire({
            icon: 'error',
            text: 'ÎπÑÌöåÏõêÏùÄ Í∏ÄÏÇ≠Ï†úÍ∞Ä Î∂àÍ∞ÄÎä•Ìï©ÎãàÎã§‚ùóÔ∏è',
            confirmButtonColor: '#FFD337',
          });
          return;
        }

        axios
          .delete(
            `${process.env.REACT_APP_API_URL}/api/community/${id}/post/${postId}`,
            {
              headers: { Authorization: localStorage.getItem('token') },
            },
          )
          .then(response => {
            Swal.fire({
              text: 'Í≤åÏãúÍ∏ÄÏù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.',
              confirmButtonColor: '#FFD337',
            }).then(result => {
              navigate(`/community/${id}`);
            });
          })
          .catch(error => {
            // ÏóêÎü¨ Ï≤òÎ¶¨
            console.log(error);
            if (error.response && error.response.status === 403) {
              Swal.fire({
                icon: 'error',
                text: 'Î≥∏Ïù∏Ïù¥ ÏûëÏÑ±Ìïú Í≤åÏãúÍ∏ÄÎßå ÏÇ≠Ï†úÌï† Ïàò ÏûàÏñ¥Ïöî‚ùóÔ∏è',
                confirmButtonColor: '#FFD337',
              });
            } else if (error.response && error.response.status === 401) {
              Swal.fire({
                icon: 'error',
                text: 'ÌÜ†ÌÅ∞Ïù¥ ÎßåÎ£åÎêòÏóàÏäµÎãàÎã§. Ïû¨Î°úÍ∑∏Ïù∏ Ìï¥Ï£ºÏÑ∏Ïöî‚ùóÔ∏è',
                confirmButtonColor: '#FFD337',
              });
              dispatch(setAuth(false));
              localStorage.removeItem('token');
            } else {
              Swal.fire({
                icon: 'error',
                text: `error! ${error}`,
                confirmButtonColor: '#FFD337',
              });
            }
          });
      }
    });
  }, [id, postId, dispatch, auth, navigate]);

  // Í∏ÄÏàòÏ†ï
  const handleEdit = useCallback(() => {
    if (!auth) {
      Swal.fire({
        icon: 'error',
        text: 'ÎπÑÌöåÏõêÏùÄ Í∏ÄÏàòÏ†ïÏù¥ Î∂àÍ∞ÄÎä•Ìï©ÎãàÎã§‚ùóÔ∏è',
        confirmButtonColor: '#FFD337',
      });
      return;
    }
    if (
      writerInfo.email === curProfile.email &&
      writerInfo.name === curProfile.name
    ) {
      navigate(`/community/${id}/write/${postId}`);
    } else {
      Swal.fire({
        icon: 'error',
        text: 'Î≥∏Ïù∏Ïù¥ ÏûëÏÑ±Ìïú Í≤åÏãúÍ∏ÄÎßå ÏàòÏ†ïÌï† Ïàò ÏûàÏñ¥Ïöî‚ùóÔ∏è',
        confirmButtonColor: '#FFD337',
      });
    }
  }, [writerInfo, curProfile, navigate, postId, id, auth]);

  // Ï¢ãÏïÑÏöî
  const isLike = () => {
    if (!localStorage.getItem('token')) {
      Swal.fire({
