import { useEffect, useState, useCallback } from 'react';
import { useNavigate, Link, useParams } from 'react-router-dom';
import { useSelector } from 'react-redux';
import axios from 'axios';
import Parser from 'html-react-parser';
import { ReactComponent as View } from '../images/view.svg';
import LikeOff from '../images/perpett-off.png';
import LikeOn from '../images/community-like-on.png';
import Button from '../components/Button/Button';
import Input from '../components/Input/Input';
import dateCalculate from '../components/dateCalculate';
import Comment from '../components/Comment';
import profile from '../images/profile.png';

function Post() {
  const [post, setPost] = useState([]);
  const [isPending, setIsPending] = useState(false);
  const [comments, setComments] = useState([]);
  const [commentInput, setCommentInput] = useState('');
  const [like, setLike] = useState(false);
  const [countLike, setCountLike] = useState(0);
  const [images, setImages] = useState([]);
  const [writerInfo, setWriterInfo] = useState([]);
  const [commentError, setCommentError] = useState('');
  const navigate = useNavigate();
  const { postId } = useParams();
  const { id } = useParams();
  const apiUrl = process.env.REACT_APP_API_URL;
  const user = useSelector(state => state.user);

  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_API_URL}/post/${postId}/comment`)
      .then(response => {
        setIsPending(true);
        // setPost(response.data[0]);
        setComments(response.data.data);
      })
      .catch(error => {
        console.log(error);
        setIsPending(false);
      });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  function changeInput(e) {
    setCommentInput(e.target.value);
    if (e.target.value.length !== 0) {
      setCommentError('');
    } else {
      setCommentError('ÎåìÍ∏Ä ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
    }
  }

  function postComment() {
    if (commentInput.length !== 0) {
      const data = {
        postId,
        content: commentInput,
      };

      axios
        .post(`${process.env.REACT_APP_API_URL}/post/${postId}/comment`, data, {
          headers: {
            Authorization: localStorage.getItem('token'),
          },
        })
        .then(response => {
          console.log(response.data);
          window.location.reload();
        })
        .catch(error => {
          console.log(error);
        });
    } else {
      setCommentError('ÎåìÍ∏Ä ÎÇ¥Ïö©ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
    }
  }

  // ÏÉÅÎã® Ïú†ÏπòÏõê Ï†ïÎ≥¥ ÏòÅÏó≠ Î∂àÎü¨Ïò§Í∏∞
  useEffect(() => {
    axios
      .get(`${apiUrl}/community/${id}/post/${postId}`)
      .then(response => {
        setWriterInfo(response.data.data);
        setPost(response.data.data);
        setCountLike(response.data.data.likeCount);
        setLike(response.data.data.like);
        setImages(response.data.data.images);
      })
      .catch(error => {
        console.log(error);
      });
  }, [setPost, postId, apiUrl, id]);

  // Í∏ÄÏÇ≠Ï†ú
  const handleDelete = useCallback(() => {
    axios
      .delete(`${apiUrl}/community/${id}/post/${postId}`, {
        headers: { Authorization: localStorage.getItem('token') },
      })
      .then(() => {
        const result = window.confirm('Í≤åÏãúÎ¨ºÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?');
        if (result) {
          alert('Í≤åÏãúÎ¨ºÏù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.');
          navigate(`/community/${id}`);
        }
      })
      .catch(error => {
        if (error.response && error.response.status === 403) {
          alert('Î≥∏Ïù∏Ïù¥ ÏûëÏÑ±Ìïú Í≤åÏãúÎ¨ºÎßå ÏÇ≠Ï†úÌï† Ïàò ÏûàÏäµÎãàÎã§.');
        } else {
          alert(`error! ${error}`);
        }
      });
  }, [apiUrl, navigate, postId, id]);

  // Í∏ÄÏàòÏ†ï
  const handleEdit = useCallback(() => {
    if (
      writerInfo.email === user[0].email &&
      user[0].name === writerInfo.name
    ) {
      navigate(`/community/${id}/write/${postId}`);
    } else {
      alert('Î≥∏Ïù∏Ïù¥ ÏûëÏÑ±Ìïú Í≤åÏãúÎ¨ºÎßå ÏàòÏ†ïÌï† Ïàò ÏûàÏäµÎãàÎã§.');
    }
  }, [writerInfo, user, navigate, postId, id]);

  // Ï¢ãÏïÑÏöî
  const isLike = () => {
    if (!localStorage.getItem('token')) {
      alert('ÎπÑÌöåÏõêÏùÄ Ï¢ãÏïÑÏöîÍ∞Ä Î∂àÍ∞ÄÎä•Ìï©ÎãàÎã§.');
      return;
      // navigate(`/login`);
    }
    const updatedLike = !like;
    const updatedLikes = updatedLike ? countLike + 1 : countLike - 1;

    setLike(updatedLike);
    setCountLike(updatedLikes);

    axios
      .get(`${apiUrl}/post/${postId}/like`, {
        headers: {
          Authorization: localStorage.getItem('token'),
        },
      })
      .then(response => {
        console.log(response);
      })
      .catch(error => {
        console.log(error);
      });
  };

  return (
    <div className="mb-64 flex flex-col items-center pt-130 onlyMobile:pt-92">
      <div className="w-full max-w-[1280px] px-80 onlyMobile:px-20">
        <div className="border-b border-solid border-black-070 pb-32">
